import React, { useState, ChangeEvent, useEffect } from 'react';
import { submitForm, fetchRefugeeById } from "../../services/apiService";

interface NewStudentRegistrationFormData {
  refugeeId: number;
  student: {
    firstName: string;
    lastName: string;
    birthDate: string;
    address: string;
    gender: string;
    school: string;
    currentGrade: string;
    languagesSpoken: string;
    countryOfOrigin: string;
  };
  parent: {
    parentName: string;
    languagesSpoken: string;
    parentPhoneNumber: string;
    parentEmail: string;
    preferredContactMethod: string;
  };
  liability: {
    insuranceLiability: boolean;
    insuranceLiabilitySignature: string;
    waiverAndRelease: boolean;
    waiverAndReleaseSignature: string;
    mediaRelease: boolean;
    mediaReleaseSignature: string;
  };
}

const NewStudentRegistrationForm: React.FC = () => {
  const [formData, setFormData] = useState<NewStudentRegistrationFormData>({
    refugeeId: 0,
    student: {
      firstName: '',
      lastName: '',
      birthDate: '',
      address: '',
      gender: '',
      school: '',
      currentGrade: '',
      languagesSpoken: '',
      countryOfOrigin: '',
    },
    parent: {
      parentName: '',
      languagesSpoken: '',
      parentPhoneNumber: '',
      parentEmail: '',
      preferredContactMethod: 'Email',
    },
    liability: {
      insuranceLiability: false,
      insuranceLiabilitySignature: '',
      waiverAndRelease: false,
      waiverAndReleaseSignature: '',
      mediaRelease: false,
      mediaReleaseSignature: '',
    },
  });
  const [error, setError] = useState<string | null>(null);

  // Fetch refugee data when refugeeId changes
  useEffect(() => {
    const fetchRefugeeData = async () => {
      if (formData.refugeeId >= 10000 && formData.refugeeId <= 99999) {
        try {
          const refugee = await fetchRefugeeById(formData.refugeeId);
          setFormData((prev) => ({
            ...prev,
            student: {
              ...prev.student,
              firstName: refugee.firstName || '',
              lastName: refugee.lastName || '',
              birthDate: refugee.dateOfBirth ? new Date(refugee.dateOfBirth).toISOString().split('T')[0] : '',
              address: refugee.address || '',
              gender: refugee.gender || '',
              countryOfOrigin: refugee.countryOfOrigin || '',
            },
          }));
          setError(null);
        } catch (err) {
          setError('Invalid Refugee ID or data not found.');
          setFormData((prev) => ({
            ...prev,
            student: {
              ...prev.student,
              firstName: '',
              lastName: '',
              birthDate: '',
              address: '',
              gender: '',
              countryOfOrigin: '',
            },
          }));
        }
      }
    };

    if (formData.refugeeId !== 0) {
      fetchRefugeeData();
    }
  }, [formData.refugeeId]);

  const handleChange = (e: ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type, checked } = e.target as HTMLInputElement;

    if (name === 'refugeeId') {
      setFormData((prev) => ({
        ...prev,
        refugeeId: parseInt(value) || 0,
      }));
    } else if (type === 'checkbox') {
      const [section, field] = name.split('.') as [keyof Omit<NewStudentRegistrationFormData, 'refugeeId'>, string];
      setFormData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [field]: checked,
        },
      }));
    } else {
      const [section, field] = name.split('.') as [keyof Omit<NewStudentRegistrationFormData, 'refugeeId'>, string];
      setFormData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [field]: value,
        },
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const formattedData = {
      ...formData,
      student: {
        ...formData.student,
        birthDate: formData.student.birthDate ? new Date(formData.student.birthDate).toISOString() : '',
      },
    };

    console.log("üì§ Submitting formatted data:", JSON.stringify(formattedData, null, 2));

    try {
      const response = await submitForm("NewStudentRegistration", formattedData);
      alert(`‚úÖ New Student Registration Form submitted successfully for Refugee ID: ${formData.refugeeId}!`);
      console.log("‚úÖ Response:", response);
    } catch (error) {
      console.error("‚ùå Error submitting form:", error);
      alert(`‚ùå Failed to submit form: ${error}`);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h1 className="text-3xl font-bold text-center mb-6">New Student Registration Form</h1>
      {error && <p className="text-red-500 text-center mb-4">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Student Details */}
        <div className="space-y-4">
          <h2 className="text-xl font-bold">Student Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">Refugee ID</label>
              <input
                type="number"
                name="refugeeId"
                value={formData.refugeeId === 0 ? '' : formData.refugeeId}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                placeholder="Enter 5-digit Refugee ID"
                required
                min="10000"
                max="99999"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">First Name</label>
              <input
                type="text"
                name="student.firstName"
                value={formData.student.firstName}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Last Name</label>
              <input
                type="text"
                name="student.lastName"
                value={formData.student.lastName}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Birth Date</label>
              <input
                type="date"
                name="student.birthDate"
                value={formData.student.birthDate}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Address</label>
              <input
                type="text"
                name="student.address"
                value={formData.student.address}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Gender</label>
              <input
                type="text"
                name="student.gender"
                value={formData.student.gender}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Country of Origin</label>
              <input
                type="text"
                name="student.countryOfOrigin"
                value={formData.student.countryOfOrigin}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md bg-gray-100"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">School</label>
              <input
                type="text"
                name="student.school"
                value={formData.student.school}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Current Grade</label>
              <input
                type="text"
                name="student.currentGrade"
                value={formData.student.currentGrade}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Languages Spoken</label>
              <input
                type="text"
                name="student.languagesSpoken"
                value={formData.student.languagesSpoken}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
          </div>
        </div>

        {/* Parent Details */}
        <div className="space-y-4">
          <h2 className="text-xl font-bold">Parent Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">Parent Name</label>
              <input
                type="text"
                name="parent.parentName"
                value={formData.parent.parentName}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Languages Spoken</label>
              <input
                type="text"
                name="parent.languagesSpoken"
                value={formData.parent.languagesSpoken}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Parent Phone Number</label>
              <input
                type="tel"
                name="parent.parentPhoneNumber"
                value={formData.parent.parentPhoneNumber}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Parent Email</label>
              <input
                type="email"
                name="parent.parentEmail"
                value={formData.parent.parentEmail}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Preferred Contact Method</label>
              <select
                name="parent.preferredContactMethod"
                value={formData.parent.preferredContactMethod}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                required
              >
                <option value="Email">Email</option>
                <option value="Phone">Phone</option>
              </select>
            </div>
          </div>
        </div>

        {/* Liability (abbreviated for brevity) */}
        <div className="space-y-4">
          <h2 className="text-xl font-bold">Liability</h2>
          <div className="space-y-2">
            <h3 className="text-lg font-bold">Insurance Liability</h3>
            <label className="flex items-center">
              <input
                type="checkbox"
                name="liability.insuranceLiability"
                checked={formData.liability.insuranceLiability}
                onChange={handleChange}
                className="mr-2"
                required
              />
              I agree...
            </label>
            <input
              type="text"
              name="liability.insuranceLiabilitySignature"
              value={formData.liability.insuranceLiabilitySignature}
              onChange={handleChange}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
              required
            />
          </div>
          {/* Add other liability sections as needed */}
        </div>

        <div className="flex justify-end">
          <button
            type="submit"
            className="bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 transition-colors"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default NewStudentRegistrationForm;
