import React, { useState, ChangeEvent } from 'react';

interface ConsentFormData {
  name: string;
  dateOfBirth: string;
  identificationNumber: string;
  address: string;
  city: string;
  state: string;
  zipCode: string;
  telephone: string;
  otherInformation: string;
  providers: {
    healthCare: boolean;
    healthCareDetails: string;
    mentalHealth: boolean;
    mentalHealthDetails: string;
    substanceUse: boolean;
    substanceUseDetails: string;
    dshsContracted: boolean;
    dshsContractedDetails: string;
    housingPrograms: boolean;
    housingProgramsDetails: string;
    schoolDistricts: boolean;
    schoolDistrictsDetails: string;
    corrections: boolean;
    correctionsDetails: string;
    employmentSecurity: boolean;
    employmentSecurityDetails: string;
    socialSecurity: boolean;
    socialSecurityDetails: string;
    other: boolean;
    otherDetails: string;
  };
  disclosureReason: {
    continuityOfCare: boolean;
    legal: boolean;
    personal: boolean;
    other: boolean;
    otherDetails: string;
  };
  recordTypes: {
    allRecords: boolean;
    attachedList: boolean;
    familyHistory: boolean;
    treatmentPlans: boolean;
    paymentRecords: boolean;
    assessments: boolean;
    education: boolean;
    mentalHealthInfo: boolean;
    mentalHealthDetails: string;
    healthCareInfo: boolean;
    healthCareDetails: string;
    other: boolean;
    otherDetails: string;
  };
  sensitiveRecords: {
    mentalHealth: boolean;
    hivAids: boolean;
    substanceUse: boolean;
  };
  consentExpiration: string;
  signature: string;
  signatureDate: string;
  witnessName: string;
  witnessSignature: string;
  witnessDate: string;
  representative: {
    signature: string;
    phone: string;
    date: string;
    type: string;
    typeOtherDetails: string;
  };
}

const DSHSConsentForm: React.FC = () => {
  const [formData, setFormData] = useState<ConsentFormData>({
    name: '',
    dateOfBirth: '',
    identificationNumber: '',
    address: '',
    city: '',
    state: 'Washington',
    zipCode: '',
    telephone: '',
    otherInformation: '',
    providers: {
      healthCare: false,
      healthCareDetails: '',
      mentalHealth: false,
      mentalHealthDetails: '',
      substanceUse: false,
      substanceUseDetails: '',
      dshsContracted: false,
      dshsContractedDetails: '',
      housingPrograms: false,
      housingProgramsDetails: '',
      schoolDistricts: false,
      schoolDistrictsDetails: '',
      corrections: false,
      correctionsDetails: '',
      employmentSecurity: false,
      employmentSecurityDetails: '',
      socialSecurity: false,
      socialSecurityDetails: '',
      other: false,
      otherDetails: ''
    },
    disclosureReason: {
      continuityOfCare: false,
      legal: false,
      personal: false,
      other: false,
      otherDetails: ''
    },
    recordTypes: {
      allRecords: false,
      attachedList: false,
      familyHistory: false,
      treatmentPlans: false,
      paymentRecords: false,
      assessments: false,
      education: false,
      mentalHealthInfo: false,
      mentalHealthDetails: '',
      healthCareInfo: false,
      healthCareDetails: '',
      other: false,
      otherDetails: ''
    },
    sensitiveRecords: {
      mentalHealth: false,
      hivAids: false,
      substanceUse: false
    },
    consentExpiration: '',
    signature: '',
    signatureDate: '',
    witnessName: '',
    witnessSignature: '',
    witnessDate: '',
    representative: {
      signature: '',
      phone: '',
      date: '',
      type: '',
      typeOtherDetails: ''
    }
  });

  const handleChange = (e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    const updateNestedState = (obj: any, path: string[], value: any): any => {
      const [current, ...rest] = path;
      if (rest.length === 0) {
        return { ...obj, [current]: value };
      }
      return {
        ...obj,
        [current]: updateNestedState(obj[current], rest, value)
      };
    };

    if (name.includes('.')) {
      const path = name.split('.');
      setFormData(prev => updateNestedState(prev, path, type === 'checkbox' ? checked : value));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : value
      }));
    }
  };

  return (
    <div style={{ maxWidth: '1000px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>CONSENT</h1>
      <p style={{ marginBottom: '20px' }}>DSHS 14-012 (REV. 03/2023)</p>

      <form>
        <div style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '20px' }}>
          <h2 style={{ marginBottom: '15px' }}>Client Identification</h2>
          <div style={{ display: 'grid', gap: '10px', gridTemplateColumns: 'repeat(3, 1fr)' }}>
            <div style={{ gridColumn: '1 / 3' }}>
              <label>NAME</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div>
              <label>DATE OF BIRTH</label>
              <input
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div style={{ gridColumn: '1 / -1' }}>
              <label>IDENTIFICATION NUMBER</label>
              <input
                type="text"
                name="identificationNumber"
                value={formData.identificationNumber}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
          </div>

          <div style={{ marginTop: '15px' }}>
            <div style={{ display: 'grid', gap: '10px', gridTemplateColumns: 'repeat(4, 1fr)' }}>
              <div style={{ gridColumn: '1 / 3' }}>
                <label>ADDRESS</label>
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
              <div>
                <label>CITY</label>
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
              <div>
                <label>ZIP CODE</label>
                <input
                  type="text"
                  name="zipCode"
                  value={formData.zipCode}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
            </div>
          </div>

          <div style={{ marginTop: '15px' }}>
            <div style={{ display: 'grid', gap: '10px', gridTemplateColumns: '1fr 2fr' }}>
              <div>
                <label>TELEPHONE NUMBER</label>
                <input
                  type="tel"
                  name="telephone"
                  value={formData.telephone}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
              <div>
                <label>OTHER INFORMATION</label>
                <input
                  type="text"
                  name="otherInformation"
                  value={formData.otherInformation}
                  onChange={handleChange}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
            </div>
          </div>
        </div>

        {/* Providers Section */}
        <div style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '20px' }}>
          <h2 style={{ marginBottom: '15px' }}>Providers</h2>
          {Object.entries({
            'Health care providers': 'healthCare',
            'Mental health care providers': 'mentalHealth',
            'Substance use disorder service providers': 'substanceUse',
            'Other DSHS contracted providers': 'dshsContracted',
            'Housing programs': 'housingPrograms',
            'School districts or colleges': 'schoolDistricts',
            'Department of Corrections': 'corrections',
            'Employment Security Department': 'employmentSecurity',
            'Social Security Administration': 'socialSecurity',
            'Other': 'other'
          }).map(([label, key]) => (
            <div key={key} style={{ marginBottom: '10px' }}>
              <label style={{ display: 'flex', alignItems: 'center' }}>
                <input
                  type="checkbox"
                  name={`providers.${key}`}
                  checked={formData.providers[key as keyof typeof formData.providers] as boolean}
                  onChange={handleChange}
                  style={{ marginRight: '8px' }}
                />
                {label}
              </label>
              {formData.providers[key as keyof typeof formData.providers] && (
                <input
                  type="text"
                  name={`providers.${key}Details`}
                  value={formData.providers[`${key}Details` as keyof typeof formData.providers] as string}
                  onChange={handleChange}
                  placeholder="Please specify"
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>
          ))}
        </div>

        {/* Sensitive Records Section */}
        <div style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '20px' }}>
          <h2 style={{ marginBottom: '15px' }}>Sensitive Records</h2>
          <p style={{ marginBottom: '10px' }}>
            Please note: If your client records include any of the following information, you must also complete this section
          </p>
          {[
            ['Mental health', 'mentalHealth'],
            ['HIV/AIDS and STD test results, diagnosis, or treatment', 'hivAids'],
            ['Substance Use Disorder', 'substanceUse']
          ].map(([label, key]) => (
            <div key={key} style={{ marginBottom: '10px' }}>
              <label style={{ display: 'flex', alignItems: 'center' }}>
                <input
                  type="checkbox"
                  name={`sensitiveRecords.${key}`}
                  checked={formData.sensitiveRecords[key as keyof typeof formData.sensitiveRecords]}
                  onChange={handleChange}
                  style={{ marginRight: '8px' }}
                />
                {label}
              </label>
            </div>
          ))}
        </div>

        {/* Signature Section */}
        <div style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '20px' }}>
          <h2 style={{ marginBottom: '15px' }}>Signatures</h2>
          <div style={{ display: 'grid', gap: '15px' }}>
            <div>
              <label>Client Signature</label>
              <input
                type="text"
                name="signature"
                value={formData.signature}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div>
              <label>Date</label>
              <input
                type="date"
                name="signatureDate"
                value={formData.signatureDate}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div>
              <label>Witness/Notary Name</label>
              <input
                type="text"
                name="witnessName"
                value={formData.witnessName}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div>
              <label>Witness/Notary Signature</label>
              <input
                type="text"
                name="witnessSignature"
                value={formData.witnessSignature}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div>
              <label>Witness/Notary Date</label>
              <input
                type="date"
                name="witnessDate"
                value={formData.witnessDate}
                onChange={handleChange}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
          </div>
        </div>

        <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '20px' }}>
          <button
            type="submit"
            style={{
              backgroundColor: '#4299e1',
              color: 'white',
              padding: '10px 20px',
              borderRadius: '4px',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default DSHSConsentForm;
